"use strict";
// Déstructuration (tableaux) Explication : Extraction des éléments d’un tableau.
// La déstructuration permet d'extraire les éléments d'un tableau et de les assigner à des variables.
// Elle est utilisée pour simplifier l'accès aux éléments d'un tableau et pour rendre le code plus lisible.
// La déstructuration est particulièrement utile lorsque l'on travaille avec des tableaux complexes ou lorsque l'on souhaite extraire plusieurs éléments à la fois.
const fruits = ["pomme", "banane", "orange"];
const [fruit1, fruit2, fruit3] = fruits; // Déstructuration du tableau fruits
console.log(fruit1); // Affiche : pomme
console.log(fruit2); // Affiche : banane
console.log(fruit3); // Affiche : orange
