"use strict";
// Explication : Type booléen (true ou false)
// Le type boolean en TypeScript est utilisé pour représenter une valeur qui peut être soit true, soit false.
// Il est souvent utilisé pour les conditions, les drapeaux d'état, ou pour contrôler le flux d'exécution dans un programme.
// Exemple d'utilisation du type boolean en TypeScript
let isActive = true; // Déclaration d'une variable de type boolean
let isComplete = false; // Une autre variable de type boolean
