"use strict";
// Explication : Permet de vérifier le type d’une variable à l’exécution.
const z = ["Drake", "Kendrick Lamar", "J Cole", 12.7, 20];
console.log(typeof z); // Affiche "object"
console.log(typeof z[0]); // Affiche "string"
console.log(typeof z[4]); // Affiche "number"
console.log(typeof z[1]); // Affiche "string"
console.log(typeof z[3]); // Affiche "number"
